sqlMap.select:
SELECT MEMO_IDX, MEMO_COLOR, MEMO_TITLE, MEMO_CONTENT, UPDATE_DATE, CREATE_DATE FROM ( SELECT MEMO_IDX, MEMO_COLOR, SUBSTR(REPLACE(REPLACE(MEMO_CONTENT, CHR(10), ''), CHR(13), ''), 0, 20) MEMO_TITLE, MEMO_CONTENT, UPDATE_DATE, CREATE_DATE , CEIL((ROW_NUMBER() OVER(ORDER BY $orderCol$ $orderType$))/$USERS_LISTNUM$) PAGENUM FROM MEMO
USERS_IDX = #USERS_IDX#
) WHERE PAGENUM = #nPage#
